package Test020Practice;

import java.util.Scanner;

public class Test016 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner s = new Scanner(System.in);

		int x = s.nextInt();

		for(int i=1;i<=x;i++)
		{

		for(int j=1;j<=i;j++)
		{
		System.out.print("* ");
		}
		System.out.println();

		}

	}

}
